async function initialize(pg, dta) {
  page = pg;
  data = dta;
}

function dumpFrameTree(frame, indent) {
  console.log(indent + frame.url());
  for (const child of frame.childFrames()) {
    dumpFrameTree(child, indent + "  ");
  }
}

async function injectBAUEagleButton() {
  try {
    const tdSelector = "#form1 > div:nth-child(1)";
    const eagleButtonSelector = "#eagleImportButton";
    await page.waitForTimeout(1000);
    const nodeExists = await page.$(tdSelector);
    if (!nodeExists) {
      return;
    }

    const isExposed = await page.evaluate("!!window.handleImportBAUMofa");
    if (!isExposed) {
      await page.exposeFunction("handleImportBAUMofa", handleImportBAUMofa);
    }

    const eagleButton = await page.$(eagleButtonSelector);
    if (!eagleButton) {
      await page.$eval(
        tdSelector,
        (el) =>
          (el.outerHTML =
            `
        <div id="eagleImportButton" style="display: flex; width: 100%; align-items: center; justify-content: center; gap: 16px; background-color: #CCCCCC; border: 2px solid black; border-radius: 16px; padding: 0.5rem;">
        <img style='width: 32px; height: 32px; border-radius: 8px; margin-right: 0.5rem'  src='data:image/jpeg;base64,'> </img> 
        <div>HAJOnSoft</div>
        <button style="color: white; background-color: forestgreen; border-radius: 16px; padding: 16px;" type="button"  onclick="handleImportBAUMofa(); return false">To Eagle (current view) النسر </button>
        </div>
        ` + el.outerHTML)
      );
    }
  } catch (err) {
    console.log("Eagle error: Wrapped", err);
  }
}

