async function onBAUPageLoad(res) {
  const mofaUrl = await page.url();
  if (!mofaUrl) {
    return;
  }

  if (
    mofaUrl.toLowerCase().includes(
      config
        .find((c) => c.name === "login")
        .url.toLowerCase()
        .replace("https://", "")
    )
  ) {
    await page.waitForSelector("#txtUserName");
    const userName = await page.$eval("#txtUserName", (el) => el && el.value);
    if (!userName) {
      await page.type("#txtUserName", data.system.username);
    }
    await page.type("#txtPassword", data.system.password);
    await page.waitForSelector("#rdCap_CaptchaTextBox");
    await page.focus("#rdCap_CaptchaTextBox");
    await util.commitCaptchaToken(
      page,
      "rdCap_CaptchaImage",
      "#rdCap_CaptchaTextBox",
      5
    );
    await page.waitForTimeout(5000);
    try {
      await page.waitForFunction(
        "document.querySelector('#rdCap_CaptchaTextBox').value.length === 5",
        { timeout: 0 }
      );
      await page.click("#lnkLogin");
    } catch {}
    return;
  }

  if (
    mofaUrl
      .toLowerCase()
      .includes("babalumra.com/Security/MainPage.aspx".toLowerCase())
  ) {
    await page.goto(
      `https://app${SERVER_NUMBER}.babalumra.com/Groups/SearchGroups.aspx`,
      { waituntill: "domcontentloaded" }
    );

    return;
  }

  if (
    mofaUrl
      .toLowerCase()
      .includes("babalumra.com/Groups/SearchGroups.aspx".toLowerCase())
  ) {
    try {
      await page.$$eval("a", (els) => {
        els.map((el) => el.removeAttribute("target"));
        els.map((el) => el.setAttribute("target", "_self"));
      });
    } catch {}
    return;
  }

  if (
    mofaUrl
      .toLowerCase()
      .includes("babalumra.com/Groups/EmbassyMutamerSearch.aspx".toLowerCase())
  ) {
    await injectBAUEagleButton3();
    return;
  }

  if (
    mofaUrl
      .toLowerCase()
      .includes("reports2.babalumra.com/ReportViewer.aspx".toLowerCase())
  ) {
    await injectBAUEagleButton();
    return;
  }

  if (
    mofaUrl
      .toLowerCase()
      .includes("babalumra.com/Groups/EmbassyMutamer.aspx".toLowerCase())
  ) {
    await injectBAUEagleButton2();
  }
}

async function importFrameContent() {
  const frameHandle = await page.waitForSelector("iframe");
  const frame = await frameHandle.contentFrame();
  const frameHtml = await frame.content();
  // find mofa data in newzealandian html
  const $ = cheerio.load(frameHtml);
  $("#Field12-0-0-0").each(function (i, elem) {
    const passportNumber = $(elem).text().trim();
    if (passports.includes(passportNumber)) {
      return;
    }

    const mofaNumber = $(elem)
      .siblings("#MDMOFAAPPROVALNO1-0-0-0")
      .eq(i)
      .text()
      .trim();
    if (passportNumber) {
      fs.writeFileSync(
        getPath(`${passportNumber}.txt`),
        JSON.stringify({ passportNumber, mofaNumber })
      );
      passports.push(passportNumber);

      // Write to Kea
      kea.updatePassenger(data.system.accountId, passportNumber, {
        mofaNumber: mofaNumber || "waiting",
      });
    }
  });
}

async function importFrameContent2() {
  const rows = await page.$$("#TblResults > tbody > tr");
  for (const row of rows) {
    const passportNumber = await row.$eval(
      "td:nth-child(3)",
      (el) => el.innerText
    );
    const mofaNumber = await row.$eval("td:nth-child(5)", (el) => el.innerText);
    const nationality = await row.$eval(
      "td:nth-child(4)",
      (el) => el.innerText
    );
    if (passportNumber && /[0-9]/.test(passportNumber)) {
      fs.writeFileSync(
        getPath(`${passportNumber}.txt`),
        JSON.stringify({ passportNumber, mofaNumber, nationality })
      );
      passports.push(passportNumber);

      // Write to Kea
      kea.updatePassenger(data.system.accountId, passportNumber, {
        mofaNumber: mofaNumber || "waiting",
      });
    }
  }
}

// @deprecated
async function importFromTableText(text) {
  const mofaRow = {};
  const lines = text.split("\n");
  for (let i = 0; i < lines.length; i++) {
    const rank = i % 8;
    const line = lines[i].trim();

    switch (rank) {
      case 0:
        if (!line.match(/^\d{5,6}$/)) {
          return; // invalid table
        }
        break;
      case 1:
        if (!line.match(/[A-Z ]+/)) {
          return; // invalid table
        }

        break;
      case 2:
        break;
      case 3:
        if (!line.match(/[A-Z0-9 ]*/)) {
          return; // invalid table
        }

        mofaRow.passportNumber = line;
        break;
      case 4:
        mofaRow.nationality = line;
        break;
      case 5:
        if (!line.match(/[0-9]{8,9}/)) {
          return; // invalid table
        }
        mofaRow.mofaNumber = line;
        if (!passports.includes(mofaRow.passportNumber)) {
          fs.writeFileSync(
            getPath(`${mofaRow.passportNumber}.txt`),
            JSON.stringify(mofaRow)
          );
          passports.push(mofaRow.passportNumber);

          // Write to Kea
          kea.updatePassenger(data.system.accountId, mofaRow.passportNumber, {
            mofaNumber: mofaRow.mofaNumber || "waiting",
          });
        }
        break;
      default:
        break;
    }
  }
}

async function handleBAUPreview() {
  const groupCode = await page.$eval(
    "#ctl00_ContentHolder_TxtGroup",
    (el) => el.value
  );

  if (groupCode) {
    await page.goto(
      `https://app${SERVER_NUMBER}.babalumra.com/Groups/EmbassyMutamer.aspx?GroupId=${groupCode}&PassId=&Emb=lasten`
    );
  }
}

// @deprecated
async function importInputContent() {
  const input = await page.$("#eagleInput");
  const text = await input.evaluate((el) => el.value);
  if (!text) {
    return;
  }
  await importFromTableText(text);
}

async function handleImportBAUMofa() {
  const beforeImport = passports.length;
  await importFrameContent();
  // await importInputContent();

  // update eagle button text
  await page.evaluate(
    (param) => {
      const eagleButton = document.querySelector(
        "#form1 > div:nth-child(1) > button"
      );
      if (param.passports.length > 0) {
        eagleButton.textContent = `Done ${param.passports.length}... [${
          param.passports[0]
        } ... ${param.passports[param.passports.length - 1]}]`;
      }
    },
    { passports, beforeImport }
  );
}

async function handleImportBAUMofa2() {
  const beforeImport = passports.length;
  importFrameContent2();

  // update eagle button text
  await page.evaluate(
    (param) => {
      const eagleButton = document.querySelector("#eagleImportButton > button");
      if (param.passports.length > 0) {
        eagleButton.textContent = `Done... [${param.passports[0]}-${
          param.passports[param.passports.length - 1]
        }] => ${param.passports.length} <= ${param.beforeImport}`;
      }
    },
    { passports, beforeImport }
  );
}

module.exports = { initialize, injectBAUEagleButton, onBAUPageLoad };
